    /* The Hook - Diffusion Visual */
    .hook-section {
      background: linear-gradient(135deg, var(--secondary-light) 0%, var(--bg-elevated) 100%);
      border-left: 4px solid var(--secondary-dark);
      margin: var(--space-6) 0;
      position: relative;
      overflow: hidden;
    }

    .diffusion-demo {
      width: 100%;
      height: 180px;
      background: var(--bg-elevated);
      display: flex;
      align-items: center;
      justify-content: center;
      gap: var(--space-8);
      padding: var(--space-6);
      border-bottom: 2px solid var(--border-light);
      position: relative;
    }

    .demo-cell {
      width: 150px;
      height: 150px;
      border: 3px solid var(--primary-dark);
      border-radius: 50%;
      background: rgba(147, 228, 249, 0.1);
      position: relative;
      display: flex;
      align-items: center;
      justify-content: center;
      font-size: var(--text-2xl);
    }

    .molecule {
      position: absolute;
      width: 12px;
      height: 12px;
      border-radius: 50%;
      background: var(--accent);
      animation: diffuse 4s infinite ease-in-out;
    }

    .molecule:nth-child(1) { animation-delay: 0s; top: 30%; left: 20%; }
    .molecule:nth-child(2) { animation-delay: 0.5s; top: 60%; left: 30%; }
    .molecule:nth-child(3) { animation-delay: 1s; top: 40%; left: 70%; }
    .molecule:nth-child(4) { animation-delay: 1.5s; top: 70%; left: 60%; }
    .molecule:nth-child(5) { animation-delay: 2s; top: 20%; left: 50%; }

    @keyframes diffuse {
      0%, 100% { transform: translate(0, 0) scale(1); opacity: 0.8; }
      25% { transform: translate(20px, -15px) scale(1.1); opacity: 1; }
      50% { transform: translate(-10px, 20px) scale(0.9); opacity: 0.9; }
      75% { transform: translate(15px, 10px) scale(1.05); opacity: 1; }
    }

    .gradient-overlay {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background: linear-gradient(90deg, rgba(255,201,150,0.3) 0%, rgba(147,228,249,0.1) 100%);
      pointer-events: none;
    }

    /* Accordion Component */
    .accordion-container {
      margin: var(--space-6) 0;
      border: 1px solid var(--border-light);
      border-radius: var(--radius-lg);
      overflow: hidden;
      background: var(--bg-surface);
    }

    .accordion-item {
      border-bottom: 1px solid var(--border-light);
    }

    .accordion-item:last-child {
      border-bottom: none;
    }

    .accordion-header {
      width: 100%;
      padding: var(--space-4) var(--space-6);
      background: var(--bg-sidebar);
      border: none;
      text-align: left;
      display: flex;
      align-items: center;
      justify-content: space-between;
      cursor: pointer;
      font-family: var(--font-primary);
      font-size: var(--text-base);
      font-weight: var(--weight-semibold);
      color: var(--text-primary);
      transition: background var(--transition-fast);
    }

    .accordion-header:hover {
      background: var(--bg-elevated);
    }

    .accordion-header[aria-expanded="true"] {
      background: var(--secondary-light);
      color: var(--text-primary);
    }

    .accordion-header[aria-expanded="true"] .accordion-icon {
      transform: rotate(180deg);
    }

    .accordion-icon {
      transition: transform var(--transition-fast);
      color: var(--text-muted);
    }

    .accordion-content {
      max-height: 0;
      overflow: hidden;
      transition: max-height var(--transition-slow);
      background: var(--bg-surface);
    }

    .accordion-content.active {
      max-height: 500px;
    }

    .accordion-body {
      padding: var(--space-6);
      color: var(--text-secondary);
      line-height: var(--leading-relaxed);
    }

    /* Retrieval Arena */
