      gap: var(--space-2);
    }

    .copy-content {
      white-space: pre-wrap;
      color: var(--text-secondary);
    }

    /* Progress indicator */
    .lesson-progress {
      position: sticky;
      top: var(--top-nav-height);
      z-index: 90;
      background: var(--bg-surface);
      border-bottom: 1px solid var(--border-light);
      padding: var(--space-2) var(--space-4);
