      <!-- 4. CORE UNIT 2: Facilitated Diffusion -->
      <section class="section" data-section="unit2">
        <h2 class="section-title">Unit 2: Facilitated Diffusion ‚Äì Protein Assistance</h2>

        <div class="content-card">
          <div class="content-card__header">
            <div class="content-card__icon" style="background: #e0e7ff; color: #4f46e5;">üö™</div>
            <h3 class="content-card__title">Channel and Carrier Proteins</h3>
          </div>
          <div class="content-card__body">
