  <!-- Scripts -->
  <script>
    // Initialize Lucide icons
    lucide.createIcons();

    // Progress Tracking
    let progress = 0;
    function updateProgress() {
      const scrollPos = window.scrollY + window.innerHeight;
      const docHeight = document.documentElement.scrollHeight;
      progress = Math.min(100, Math.round((scrollPos / docHeight) * 100));

      document.getElementById('progressFill').style.width = progress + '%';
      document.getElementById('progressText').textContent = progress + '%';
    }
    window.addEventListener('scroll', updateProgress);

    // Accordion
    function toggleAccordion(button) {
      const content = button.nextElementSibling;
      const isExpanded = button.getAttribute('aria-expanded') === 'true';

      button.setAttribute('aria-expanded', !isExpanded);
      content.classList.toggle('active');

      document.querySelectorAll('.accordion-header').forEach(other => {
        if (other !== button && other.getAttribute('aria-expanded') === 'true') {
          other.setAttribute('aria-expanded', 'false');
          other.nextElementSibling.classList.remove('active');
        }
      });
    }

    // Checkpoint Quiz
    function checkCheckpoint(element, isCorrect) {
      const feedback = document.getElementById('checkpoint-feedback');
      feedback.style.display = 'block';

      if (isCorrect) {
        feedback.innerHTML = '<span style="color: #059669;">‚úì Correct! Oxygen moves down its concentration gradient (21% outside ‚Üí 2% inside).</span>';
        element.style.background = '#d1fae5';
      } else {
        feedback.innerHTML = '<span style="color: #dc2626;">‚úó Remember: substances move from HIGH to LOW concentration.</span>';
        element.style.background = '#fee2e2';
      }

      document.querySelectorAll('.checkpoint .mc-option').forEach(opt => {
        opt.style.pointerEvents = 'none';
      });
    }

    // Sorting Activity
    function checkSorting() {
      const answers = { 'sort-q1': 'S', 'sort-q2': 'F', 'sort-q3': 'F' };
      let correct = 0;

      Object.entries(answers).forEach(([id, answer]) => {
        const select = document.getElementById(id);
        if (select.value === answer) {
          select.style.borderColor = '#059669';
          select.style.background = '#d1fae5';
          correct++;
        } else {
          select.style.borderColor = '#dc2626';
          select.style.background = '#fee2e2';
        }
      });

      const result = document.getElementById('sorting-result');
      if (correct === 3) {
        result.innerHTML = '<span style="color: #059669; font-weight: 600;">‚úì All correct! O‚ÇÇ is small & nonpolar (simple), while water via aquaporin and glucose both use proteins (facilitated).</span>';
      } else {
        result.innerHTML = '<span style="color: #dc2626; font-weight: 600;">‚úó ' + correct + '/3 correct. Remember: if a protein is involved, it\'s facilitated diffusion.</span>';
      }
    }

    // Cloze Activity
    function checkCloze() {
      const inputs = document.querySelectorAll('.cloze-input');
      let correct = 0;

      inputs.forEach(input => {
        const answer = input.dataset.answer.toLowerCase();
        const userAnswer = input.value.toLowerCase().trim();

        if (userAnswer === answer) {
          input.style.borderColor = '#059669';
          input.style.background = '#d1fae5';
          correct++;
        } else {
          input.style.borderColor = '#dc2626';
          input.style.background = '#fee2e2';
        }
      });

      const result = document.getElementById('cloze-result');
      if (correct === inputs.length) {
        result.innerHTML = '<span style="color: #059669; font-weight: 600;">‚úì Perfect! All blanks correct.</span>';
      } else {
        result.innerHTML = '<span style="color: #dc2626; font-weight: 600;">‚úó ' + correct + '/' + inputs.length + ' correct. Review the lesson content and try again.</span>';
      }
    }

    // Mark Complete
    function markLessonComplete() {
      const btn = document.getElementById('mark-complete-btn');
      const msg = document.getElementById('completion-message');
      const nextBtn = document.getElementById('nextLessonBtn');

      btn.classList.add('completed');
      btn.innerHTML = '<i data-lucide="check-circle" class="icon-sm"></i><span>Completed!</span>';
      lucide.createIcons();

      msg.style.display = 'block';
      msg.innerHTML = 'üéâ Mastered Passive Transport! Ready for Active Transport (Lesson 6).';

      nextBtn.style.opacity = '1';
      nextBtn.style.pointerEvents = 'auto';

      localStorage.setItem('lesson5_complete', 'true');
      localStorage.setItem('last_lesson', 'lesson-5');

      document.querySelector('.lesson-item.active').classList.add('completed');
    }

    // Mobile sidebar
    function toggleSidebar() {
      document.getElementById('lessonSidebar').classList.toggle('open');
    }

    // Check completion on load
    window.addEventListener('load', () => {
      if (localStorage.getItem('lesson5_complete') === 'true') {
        document.getElementById('mark-complete-btn').classList.add('completed');
        document.getElementById('mark-complete-btn').innerHTML = '<i data-lucide="check-circle" class="icon-sm"></i><span>Completed</span>';
        document.getElementById('nextLessonBtn').style.opacity = '1';
        document.getElementById('nextLessonBtn').style.pointerEvents = 'auto';
      }
      lucide.createIcons();
    });
  </script>
